import React, { useEffect, useState } from 'react';

const UseEffect4 = () => {
    const [countDown, setCountDown] = useState(0);

    // useEffect(()=>{
    //     const interval= setInterval(()=>{
    //         setCountDown(preState=>preState-1);
    //     }, 1000)

    //     // huy ham neu gia tri am or khong duong
    //     return ()=>clearInterval(interval);
    // }, []) //Dependencies là một mảng rỗng, chỉ chạy một lần khi component mount


    const handleInput=(event) => {
        setCountDown(parseInt(event.target.value));
    }

    const handleEnter = (event) => {
        if(event.key==='Enter' && countDown>0) {
            if (event.key === 'Enter' && countDown > 0) {
                // Bắt đầu đếm ngược chỉ khi nhấn Enter và countDown lớn hơn 0
                const interval = setInterval(() => {
                    setCountDown(prevCount => prevCount - 1);
                }, 1000);

                // Hủy interval khi countDown đạt giá trị âm
                if (countDown === 1) {
                    clearInterval(interval);
                }
            }
        }
    }



    return (
        <div>
            <input
                type='number'
                value={countDown}
                className='border border-red-400'
                onChange={handleInput}
            />
            <button onKeyDown={handleEnter}>
                Start Count Down
            </button>

            <h1>countDown</h1>
        </div>
    );
}

export default UseEffect4;
